const mongoose = require('mongoose');

const orderServiceSchema = new mongoose.Schema({
    numeroOS: {
        type: String,
        required: true,
        unique: true
    },
    dateOS: {
        type: Date,
        required: true
    },
    typeOS: {
        type: String,
        required: true,
        enum: ['Premier OS', 'Arret', 'Reprise', 'Autres']
    },
    fichier: {
        filename: {
            type: String,
            default: 'Aucun'
        },
        path: {
            type: String,
            default: ''
        }
    }
}, {
    timestamps: true
});

module.exports = mongoose.model('OrderService', orderServiceSchema); 